configuration.scripts:

$(document).ready(function() {
    // ===== ENHANCED EXTENT REPORTS JAVASCRIPT FEATURES =====
    addLoadingAnimations();
    enhanceSearchFeatures();
    addSmoothScrolling();
    initializeTooltips();
    addCopyToClipboard();
    updateRealTimeStats();
    enhanceScreenshotViewing();
    addKeyboardShortcuts();
    autoExpandFailedTests();
    console.log('ðŸš€ Job Mapping - Enhanced Extent Reports Loaded Successfully!');
});

function addLoadingAnimations() {
    $('.test-node').each(function(index) {
        $(this).css('animation-delay', (index * 100) + 'ms').addClass('fade-in-animation');
    });
    $('.card').hover(
        function() { $(this).addClass('card-hover-effect'); },
        function() { $(this).removeClass('card-hover-effect'); }
    );
}

function enhanceSearchFeatures() {
    $('#search-tests').on('input', function() {
        const searchTerm = $(this).val().toLowerCase();
        let visibleCount = 0;
        $('.test-node').each(function() {
            const testText = $(this).text().toLowerCase();
            if (testText.includes(searchTerm) || searchTerm === '') {
                $(this).show(); visibleCount++;
            } else {
                $(this).hide();
            }
        });
    });
}

function addSmoothScrolling() {
    $('a[href*="#"]').click(function(e) {
        const target = $(this.hash);
        if (target.length) {
            e.preventDefault();
            $('html, body').animate({scrollTop: target.offset().top - 100}, 800);
        }
    });
}

function initializeTooltips() {
    $('.test-node .badge').each(function() {
        const status = $(this).text().trim();
        let tooltip = status === 'pass' ? 'âœ… Success' : status === 'fail' ? 'âŒ Failed' : 'âš ï¸ Skipped';
        $(this).attr('title', tooltip);
    });
}

function addCopyToClipboard() {
    $('.test-content pre, .stacktrace').each(function() {
        const $pre = $(this);
        const $copyBtn = $('<button class="copy-btn btn btn-sm" title="Copy">ðŸ“‹</button>');
        $copyBtn.click(function() {
            navigator.clipboard.writeText($pre.text());
            $copyBtn.text('âœ…');
            setTimeout(() => $copyBtn.text('ðŸ“‹'), 2000);
        });
        $pre.css('position', 'relative').append($copyBtn);
    });
}

function updateRealTimeStats() {
    const total = $('.test-node').length;
    const passed = $('.test-node.pass').length;
    const failed = $('.test-node.fail').length;
    const skipped = $('.test-node.skip').length;
    const passRate = total > 0 ? Math.round((passed / total) * 100) : 0;

    const statsHtml = `
        <div class="enhanced-stats-panel card mb-3">
            <div class="card-header"><h5>ðŸ“Š Execution Summary</h5></div>
            <div class="card-body">
                <div class="row text-center">
                    <div class="col-3"><div class="stat-box">${passed} Passed (${passRate}%)</div></div>
                    <div class="col-3"><div class="stat-box">${failed} Failed</div></div>
                    <div class="col-3"><div class="stat-box">${skipped} Skipped</div></div>
                    <div class="col-3"><div class="stat-box">${total} Total</div></div>
                </div>
            </div>
        </div>`;
    $('.test-content').prepend(statsHtml);
}

function enhanceScreenshotViewing() {
    $('img[src*="screenshot"], img[src*="Screenshot"]').click(function() {
        const imgSrc = $(this).attr('src');
        const modal = `<div class="screenshot-modal" style="position:fixed;top:0;left:0;width:100%;height:100%;background:rgba(0,0,0,0.9);z-index:9999;display:flex;justify-content:center;align-items:center;"><img src="${imgSrc}" style="max-width:90%;max-height:90%;border-radius:8px;"></div>`;
        $('body').append(modal);
        $('.screenshot-modal').click(function() { $(this).remove(); });
    });
}

function addKeyboardShortcuts() {
    $(document).keydown(function(e) {
        if (e.key === 'Escape') $('.screenshot-modal').remove();
        if ((e.ctrlKey || e.metaKey) && e.key === 'f') {
            e.preventDefault(); $('#search-tests').focus();
        }
    });
}

function autoExpandFailedTests() {
    $('.test-node.fail').slice(0, 2).each(function() {
        $(this).find('.collapsible').click();
    });
}

